 -- Table used in the STDF Recovery flow
 CREATE TABLE "MANUFDATA"."CLOUD_RECOVERY" 
   (	
    "CLOUD_RECOVERY_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 999999999 INCREMENT BY 1 START WITH 1 CACHE 50 NOORDER  CYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
	"DATA_FILE_ID" NUMBER, 
	"RECOVERY_DATE" DATE, 
	"USER_NAME" VARCHAR2(100 BYTE),
	"EMAIL" VARCHAR2(200 BYTE), 
	"STATUS" VARCHAR2(50 BYTE),	
	"REQUEST_ID" VARCHAR2(100 BYTE),
	CONSTRAINT "CLOUD_RECOVERY_PK" PRIMARY KEY ("CLOUD_RECOVERY_ID"), 
  CONSTRAINT "FK_CLOUD_RECOVERY_DATA_FILE" FOREIGN KEY ("DATA_FILE_ID")
	  REFERENCES "MANUFDATA"."DATA_FILE" ("DATA_FILE_ID") ENABLE
   )  ;
  GRANT SELECT ON "MANUFDATA"."CLOUD_RECOVERY" TO "MANUFAPPS";
  
  
 -- Table used in the STDF File Upload flow
  CREATE TABLE "MANUFDATA"."CLOUD_STORAGE_FILE" 
   (	
    "CLOUD_STORAGE_FILE_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 999999999 INCREMENT BY 1 START WITH 1 CACHE 50 NOORDER  CYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
	"DATA_FILE_ID" NUMBER, 
	"COMPRESSED_NAME" VARCHAR2(500 BYTE), 
	"ETAG" VARCHAR2(100 BYTE), 
	"LOCATION" VARCHAR2(100 BYTE), 
	"STORAGE_TYPE" VARCHAR2(50 BYTE), 
	"FILESIZE" NUMBER, 
	"INITIAL_UPLOAD_DATE" DATE, 
	"MODIFIED_DATE" DATE, 
	"NEW_STORAGE_TYPE" CHAR(1 BYTE), 
	"NEW_LOCATION" VARCHAR2(100 BYTE), 
	"JOB_TYPE" VARCHAR2(100 BYTE), 
	 CONSTRAINT "CLOUD_STORAGE_FILE_PK" PRIMARY KEY ("CLOUD_STORAGE_FILE_ID"), 
	 CONSTRAINT "CLOUD_STORAGE_FILE_UK" UNIQUE ("COMPRESSED_NAME"), 
	 CONSTRAINT "FK_CLOUD_STORAGE_FILE_DATA_FILE" FOREIGN KEY ("DATA_FILE_ID")
	  REFERENCES "MANUFDATA"."DATA_FILE" ("DATA_FILE_ID") ENABLE
   )  ;
     GRANT SELECT ON "MANUFDATA"."CLOUD_STORAGE_FILE" TO "MANUFAPPS";
     
   
  -- Table used in the Part Dies Flow 
  CREATE TABLE "MANUFDATA"."PART_DIE_LOCATION" 
   (	
    "PART_DIES_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 999999999 INCREMENT BY 1 START WITH 1 CACHE 50 NOORDER  CYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
	"PART_ID" NUMBER, 
	"X" NUMBER, 
	"Y" NUMBER, 
	 CONSTRAINT "PART_DIES_PK" PRIMARY KEY ("PART_DIES_ID"), 
	 CONSTRAINT "PART_DIE_LOCATION_UK" UNIQUE ("PART_ID", "X", "Y"), 
	 CONSTRAINT "PART_DIES_PART_FK" FOREIGN KEY ("PART_ID")
	  REFERENCES "MANUFDATA"."PART" ("PART_ID") ENABLE
   ) ;
  GRANT SELECT ON "MANUFDATA"."PART_DIE_LOCATION" TO "MANUFAPPS";
  
  
  -- Table used in the Wafer Merge Flow 
  CREATE TABLE "MANUFDATA"."INVOKE_MERGE" 
   (	
    "INVOKE_MERGE_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 999999999 INCREMENT BY 1 START WITH 1 CACHE 50 NOORDER  CYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
	"WAFER_INSERTION_ID" NUMBER, 
	"LAST_UPDATE_DATE" DATE, 
	"USER_NAME" VARCHAR2(100 BYTE), 
	"EMAIL" VARCHAR2(200 BYTE), 
	"STATUS" VARCHAR2(50 BYTE), 
	 CONSTRAINT "INVOKE_MERGE_PK" PRIMARY KEY ("INVOKE_MERGE_ID"), 
	 CONSTRAINT "FK_INVOKE_MERGE_WAFER_INSERTION" FOREIGN KEY ("WAFER_INSERTION_ID")
	  REFERENCES "MANUFDATA"."WAFER_INSERTION" ("WAFER_INSERTION_ID") ENABLE
   ) ;
  GRANT SELECT ON "MANUFDATA"."INVOKE_MERGE" TO "MANUFAPPS";